$(document).ready(function(){$('input, textarea').placeholder();$(".video-container").fitVids();$('#bg-slider').flexslider({animation:"fade",directionNav:false,controlNav:false,slideshowSpeed:6000});function toggleIcon(e){$(e.target).prev('.panel-heading').find('.panel-title a').toggleClass('active').find("i.fa").toggleClass('fa-plus-square fa-minus-square');}$('.panel').on('hidden.bs.collapse',toggleIcon);$('.panel').on('shown.bs.collapse',toggleIcon);$(window).bind('scroll',function(){if($(window).scrollTop()>0){$('#header').addClass('navbar-fixed-top');}else{$('#header').removeClass('navbar-fixed-top');}});$('#signup-link').on('click',function(e){$('#login-modal').modal('toggle');$('#signup-modal').modal();e.preventDefault();});$('#login-link').on('click',function(e){$('#signup-modal').modal('toggle');$('#login-modal').modal();e.preventDefault();});$('#back-to-login-link').on('click',function(e){$('#resetpass-modal').modal('toggle');$('#login-modal').modal();e.preventDefault();});$('#resetpass-link').on('click',function(e){$('#login-modal').modal('hide');e.preventDefault();});$('#price-plan .btn-cta').on('click',function(e){$('#signup-modal').modal();e.preventDefault();});$('#config-trigger').on('click',function(e){var $panel=$('#config-panel');var panelVisible=$('#config-panel').is(':visible');if(panelVisible){$panel.hide();}else{$panel.show();}e.preventDefault();});$('#config-close').on('click',function(e){e.preventDefault();$('#config-panel').hide();});$('#color-options a').on('click',function(e){var $styleSheet=$(this).attr('data-style');$('#theme-style').attr('href',$styleSheet);var $listItem=$(this).closest('li');$listItem.addClass('active');$listItem.siblings().removeClass('active');e.preventDefault();});});